import React from "react";

const style = {
  backgroundColor: "rgb(240, 193, 233)",
  textAlign: "center",
  borderRadius: "5px",
};

const inputStyle = {
  fontSize: "12px",
  border: "none",
  borderRadius: "10px",
  width: "250px",
  height: "25px",
  margin: "3px 0",
  outline: "none"
};

const selectStyle = {
  height: "25px",
  borderRadius: "10px"
};



export const InputTodo = (props) => {
  const { todoText, onChange, place, food, handleChange } = props;
  return (
    <div style={style} className="phoneStyle">
        <input
          key={todoText}
          placeholder="メモを入力"
          value={todoText}
          onChange={onChange}
          style={inputStyle}
          className='inputMemo'
        />
      <div>
        <button onClick={place}>行きたいところに追加</button>
        <button onClick={food}>グルメに追加</button>
        <select 
          name="Italy-places" 
          // value={["Milan","Venezia","Dolomiti","Firenze","Rome","Naples","Sicily"]} 
          id="Italy-places" 
          style={selectStyle} 
          onChange={handleChange}>
            <option value="Milan">Milan</option>
            <option value="Venezia">Venezia</option>
            <option value="Dolomiti">Dolomiti</option>
            <option value="Firenze">Firenze</option>
            <option value="Rome">Rome</option>
            <option value="Naples">Naples</option>
            <option value="Sicily">Sicily</option>
        </select>
      </div>
    </div>
  );
};
